import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function Chat({ socket }) {
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    // socket 객체가 유효한지 확인
    if (socket) {
      socket.emit("create_ai_chat_room");

      const handleNewMessage = (text) => {
        // 서버로부터 수신한 메시지가 단순 문자열이라고 가정할 때,
        // UI에서 기대하는 객체 형태로 변환합니다.
        const newMessageObject = {
          user: "AI", // 'User'는 예시로 사용한 값입니다. 실제 사용자 이름이나 ID 등으로 대체해야 합니다.
          message: text,
        };
        setChat((prevChat) => [...prevChat, newMessageObject]);
      };

      socket.on("ai_chat_message", handleNewMessage);

      // 컴포넌트 언마운트 시 리스너 정리
      return () => {
        socket.off("ai_chat_message", handleNewMessage);
      };
    }
  }, [socket]);

  const sendMessage = (event) => {
    event.preventDefault();
    if (socket && message.trim()) {
      // 사용자가 보낸 메시지를 UI에 즉시 표시하기 위해 chat 배열에 추가합니다.
      const newMessageObject = {
        user: "U", // 현재 사용자를 나타내는 텍스트로 'Me'를 사용합니다.
        message: message,
      };
      setChat((prevChat) => [...prevChat, newMessageObject]);

      // 메시지를 서버로 보냅니다.
      socket.emit("ai_chat_message", message);
      setMessage(""); // 입력 필드를 비웁니다.
    }
  };
  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col xs lg="6">
          <Card
            className="mt-5"
            style={{
              height: "500px",
              border: "none",
              borderRadius: "20px",
              boxShadow: "0px 10px 20px rgba(0, 0, 0, 0.1)",
              overflow: "auto",
            }}
          >
            <Card.Body>
              <Card.Title
                className="text-center"
                style={{ fontSize: "2rem", fontWeight: "bold", color: "#333" }}
              >
                CHAIRS Chat
              </Card.Title>
              <div style={{ height: "70%", overflowY: "scroll" }}>
                {chat.map((chat, index) => (
                  <div
                    key={index}
                    style={{
                      textAlign: chat.user === "User" ? "right" : "left",
                    }}
                  >
                    <p>
                      <strong>{chat.user}:</strong> {chat.message}
                    </p>
                  </div>
                ))}
              </div>
              <Form className="mt-3" onSubmit={sendMessage}>
                <Form.Group controlId="chatInput" className="d-flex">
                  <Form.Control
                    type="text"
                    placeholder="Type your message here..."
                    className="mr-2"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                  />
                  <Button variant="primary" type="submit">
                    Send
                  </Button>
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Chat;
