import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function Chat() {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col xs lg="6">
          <Card
            className="mt-5"
            style={{
              height: "500px",
              border: "none",
              borderRadius: "20px",
              boxShadow: "0px 10px 20px rgba(0, 0, 0, 0.1)",
              overflow: "auto",
            }}
          >
            <Card.Body>
              <Card.Title
                className="text-center"
                style={{ fontSize: "2rem", fontWeight: "bold", color: "#333" }}
              >
                CHAIRS Chat {/* 로고 텍스트 */}
              </Card.Title>
              {/* 채팅 메시지가 표시되는 영역 */}
              <div style={{ height: "70%", overflowY: "scroll" }}>
                {/* 채팅 메시지 예시 */}
                <div style={{ textAlign: "right" }}>
                  <p>
                    <strong>User:</strong> Hello, AI!
                  </p>
                </div>
                <div style={{ textAlign: "left" }}>
                  <p>
                    <strong>AI:</strong> Hello, User! How can I assist you
                    today?
                  </p>
                </div>
                {/* ... */}
              </div>
              {/* 채팅 입력 영역 */}
              <Form className="mt-3">
                <Form.Group controlId="chatInput" className="d-flex">
                  <Form.Control
                    type="text"
                    placeholder="Type your message here..."
                    className="mr-2"
                  />
                  <Button variant="primary" type="submit">
                    Send
                  </Button>
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Chat;
